import React, { Suspense, useRef, useState } from 'react'
import { PerspectiveCamera, Html, useGLTF } from '@react-three/drei'
import { Canvas, useFrame } from '@react-three/fiber'
import * as THREE from 'three'
import './ThreeElement.scss'

export const Model = () => {
  const gltf = useGLTF('/desk_low-poly/scene.gltf', true)
  return <primitive object={gltf.scene} dispose={null} />
}

function ThreeModel(props) {
  const [clicked, setClicked] = useState(false)
  const vec = new THREE.Vector3()
  const ref = useRef()
  useFrame((state) => {
    if (clicked) {
      state.camera.lookAt(ref.current.position)
      state.camera.position.lerp(vec.set(xPosition, yPosition, zPosition), 0.01)
      state.camera.updateProjectionMatrix()
    }
    return null
  })
  return (
    <mesh
      ref={ref}
      position={[0, 0, 0]}
      rotation={[0, -1.6, 0]}
      onClick={() => setClicked(!clicked)}
    >
      <Model />
    </mesh>
  )
}

function ThreeElement(props) {
  const [mouseDown, setMouseDown] = useState(false)

  /*let timestamp = 0
  let mX = 0
  const handleMouseMove = (e) => {
    if (mouseDown) {
      let multiplier = 1
      let now = Date.now()
      let currentmX = e.screenX
      let dt = now - timestamp
      if (currentmX < mX) {
        multiplier = -1
      }
      let distance = Math.abs(currentmX - mX)
      let speed = (Math.round(distance / dt) / 30) * multiplier
      ref.current.rotation.y += speed
      mX = currentmX
      timestamp = now
    }
  }*/

  return (
    <div className='Three-Container'>
      <Canvas
      /*onMouseDown={() => {
          setMouseDown(true)
        }}
        onMouseUp={() => {
          setMouseDown(false)
        }}
        onMouseMove={handleMouseMove}
        id='three-canvas'*/
      >
        <ambientLight intensity={0.7} />
        <spotLight intensity={0.5} position={[800, 1000, 3000]} />

        <PerspectiveCamera
          makeDefault
          position={[0, 420, 1000]}
          rotation={[-0.3, 0, 0]}
          fov='25'
        />

        <Suspense fallback={null}>{ThreeModel}</Suspense>
      </Canvas>
    </div>
  )
}

export default ThreeElement
