import React, { Suspense, useRef, useState } from 'react'
import { PerspectiveCamera, Html, useGLTF } from '@react-three/drei'
import { Canvas, useFrame } from '@react-three/fiber'
import './ThreeElement.scss'

export const Model = () => {
  const gltf = useGLTF('/desk_low-poly/scene.gltf', true)
  return <primitive object={gltf.scene} dispose={null} />
}

function Box(props) {
  const mesh = useRef()
  return (
    <mesh {...props} ref={mesh}>
      <boxGeometry args={[3, 3, 3]} />
      <meshStandardMaterial color={'orange'} />
    </mesh>
  )
}

function ThreeElement(props) {
  const [mouseDown, setMouseDown] = useState(false)

  let timestamp = 0
  let mX = 0
  const handleMouseMove = (e) => {
    if (mouseDown) {
      let multiplier = 1
      let now = Date.now()
      let currentmX = e.screenX
      let dt = now - timestamp
      if (currentmX < mX) {
        multiplier = -1
      }
      let distance = Math.abs(currentmX - mX)
      let speed = (Math.round(distance / dt) / 30) * multiplier
      ref.current.rotation.y += speed
      mX = currentmX
      timestamp = now
    }
  }

  const ref = useRef()

  /*return (
    <div className='Three-Container'>
      <Canvas
        onMouseDown={() => {
          setMouseDown(true)
        }}
        onMouseUp={() => {
          setMouseDown(false)
        }}
        onMouseMove={handleMouseMove}
        id='three-canvas'
      >
        <PerspectiveCamera
          makeDefault
          position={[0, 0, 0]}
          rotation={[-0.4, 0, 0]}
        />
        <ambientLight intensity={1} />
        <Suspense fallback={null}>
          <mesh ref={ref} position={[0, 0, 0]} rotation={[0, 0, 0]}>
            <Model />
          </mesh>
        </Suspense>
      </Canvas>
    </div>
  )*/
  return (
    <div className='Three-Container'>
      <Canvas>
        <ambientLight />
        <PerspectiveCamera
          makeDefault
          position={[0, 400, 1000]}
          rotation={[-0.3, 0, 0]}
          fov='35'
        />
        <mesh ref={ref} position={[0, 0, 0]} rotation={[0, -1.6, 0]}>
          <Model />
        </mesh>
      </Canvas>
    </div>
  )
}

export default ThreeElement
