import React, { Suspense, useEffect, useRef, useState } from 'react'
import { PerspectiveCamera, Html, useGLTF } from '@react-three/drei'
import { Canvas, useFrame } from '@react-three/fiber'
import './ThreeElement.scss'

export const Model = () => {
  const gltf = useGLTF('/desk_low-poly/scene.gltf', true)
  return <primitive object={gltf.scene} dispose={null} />
}

function ThreeModel(props) {
  const [mouseDown, setMouseDown] = useState(false)
  const [active, setActive] = useState(false)
  const ref = useRef()

  let timestamp = 0
  let mX = 0
  const handleMouseMove = (e) => {
    if (mouseDown) {
      let multiplier = 1
      let now = Date.now()
      let currentmX = e.screenX
      let dt = now - timestamp
      if (currentmX < mX) {
        multiplier = -1
      }
      let distance = Math.abs(currentmX - mX)
      let speed = (Math.round(distance / dt) / 30) * multiplier
      ref.current.rotation.y += speed
      mX = currentmX
      timestamp = now
    }
  }

  useEffect(() => {
    const timer = setTimeout(() => {
      setActive(true)
    }, 400)
    return () => {
      clearTimeout(timer)
      setActive(false)
    }
  }, [])

  useFrame((state) => {
    if (ref.current.rotation.y > -1.6 && active) {
      ref.current.rotation.y -= 0.015
    }
  })
  return (
    <mesh
      ref={ref}
      position={[0, 0, 0]}
      rotation={[0, 1.6, 0]}
      onMouseDown={() => {
        setMouseDown(true)
      }}
      onMouseUp={() => {
        setMouseDown(false)
      }}
      onMouseMove={handleMouseMove}
    >
      <Model />
    </mesh>
  )
}

function ThreeElement(props) {
  return (
    <div className='Three-Container'>
      <Canvas>
        <ambientLight intensity={0.7} />
        <directionalLight intensity={0.5} position={[800, 1000, 3000]} />
        <directionalLight intensity={0.7} position={[0, 1000, 0]} />
        <PerspectiveCamera
          makeDefault
          lookAt={(0, 0, 0)}
          position={[0, 420, 1000]}
          rotation={[-0.3, 0, 0]}
          fov='25'
        />
        <Suspense fallback={null}>
          <Html transform position={[-98, 338, 0]}>
            <div className='Moving-Icon-Container'>
              <div className='Moving-Icon'>[ JS ]</div>
            </div>
          </Html>
          <ThreeModel />
        </Suspense>
      </Canvas>
    </div>
  )
}

export default ThreeElement
